    private void setNavigationView(Toolbar toolbar, Bundle savedInstanceState) {
        drawerHeaderView = new DrawerHeaderView(this);
        String[] menus = getResources().getStringArray(R.array.menuList);
        DrawerBuilder drawerBuilder = new DrawerBuilder()
                .withActivity(this)
                .withRootView(R.id.drawer_container)
                .withToolbar(toolbar)
                .withHasStableIds(true)
                .withDisplayBelowStatusBar(true)
                .withActionBarDrawerToggle(true)
                .withHeader(drawerHeaderView)
                .withHeaderPadding(true)
                .addDrawerItems(

                ) // add the items we want to use with our Drawer
                .withOnDrawerItemClickListener(new Drawer.OnDrawerItemClickListener() {
                    @Override
                    public boolean onItemClick(View view, int position, IDrawerItem drawerItem) {
                        //check if the drawerItem is set.
                        //there are different reasons for the drawerItem to be null
                        //--> click on the header
                        //--> click on the footer
                        //those items don't contain a drawerItem

                        if (drawerItem != null) {
                            setFragmentToDisplay(Long.valueOf(drawerItem.getIdentifier()).intValue(), null, false);
                        }

                        return false;
                    }
                })
                .withSavedInstance(savedInstanceState)
                .withShowDrawerOnFirstLaunch(false);

        List<IDrawerItem> primaryDrawerItems = new ArrayList<>();
        primaryDrawerItems.add(new PrimaryDrawerItem().withName(menus[0]).withIcon(R.drawable.ic_home_black_24dp).withIdentifier(AppConstants.FRAGMENT_DASHBOARD));
        primaryDrawerItems.add(new PrimaryDrawerItem().withName(menus[1]).withIcon(R.drawable.ic_orderlist_24).withIdentifier(AppConstants.FRAGMENT_ORDER));
        primaryDrawerItems.add(new PrimaryDrawerItem().withName(menus[2]).withIcon(R.drawable.ic_profile_24).withSetSelected(false).withIdentifier(AppConstants.FRAGMENT_PROFILE));
        primaryDrawerItems.add(new PrimaryDrawerItem().withName(menus[3]).withIcon(R.drawable.ic_help_24).withIdentifier(AppConstants.FRAGMENT_FEEDBACK));
        if (AppConstants.isAdmin) {
            primaryDrawerItems.add(new DividerDrawerItem());
            primaryDrawerItems.add(new PrimaryDrawerItem().withName(menus[4]).withIcon(R.drawable.ic_help_24).withIdentifier(AppConstants.FRAGMENT_RECIEVED_ORDERS));
            primaryDrawerItems.add(new PrimaryDrawerItem().withName(menus[5]).withIcon(R.drawable.ic_help_24).withIdentifier(AppConstants.FRAGMENT_ADDPRODUCT));
        }
        primaryDrawerItems.add(new DividerDrawerItem());
        primaryDrawerItems.add(new PrimaryDrawerItem().withName(menus[6]).withIcon(R.drawable.ic_logout_24).withIdentifier(AppConstants.FRAGMENT_LOGOUT));


        drawerBuilder.withDrawerItems(primaryDrawerItems);
        result = drawerBuilder.build();

//        new RecyclerViewCacheUtil<IDrawerItem>().withCacheSize(2).apply(result.getRecyclerView(), result.getDrawerItems());
        result.setSelection(1, false);


        result.setSelectionAtPosition(0);
        if (savedInstanceState == null) {
            // set the selection to the item with the identifier 11
            result.setSelection(1, false);
            setFragmentToDisplay(1, null, false);

            //set the active profile
//            headerResult.setActiveProfile(profile3);
        }
    }

    private void setFragmentToDisplay(int index, Bundle data, boolean isBackPressed) {
        Fragment fragment = null;
        FragmentManager fragmentManager = getSupportFragmentManager();
        FragmentTransaction fragmentTransaction = fragmentManager.beginTransaction();
        String fragmentTag = "";
        switch (index) {
            case AppConstants.FRAGMENT_DASHBOARD:
                fragment = new MainDashboardFragment(productModelArrayList, this, this);
                fragmentTag = "dashboardFragment";
//                title.setText(G7Constants.DASHBOARD_TITLE);
                break;
            case AppConstants.FRAGMENT_LOGOUT:
                FirebaseAuth.getInstance().signOut();
                SharedPreferences sharedPreferences = getSharedPreferences(AppConstants.sharedPreferences, MODE_PRIVATE);
                sharedPreferences.edit().clear().apply();
                Intent intentLogout = new Intent(DashboardActivity.this, LoginActivity.class);
                startActivity(intentLogout);
                finish();
                break;
            case AppConstants.FRAGMENT_ORDER:
                Intent intentYourOrder = new Intent(DashboardActivity.this, YourOrdersActivity.class);
                startActivity(intentYourOrder);
                break;

            case AppConstants.FRAGMENT_FEEDBACK:
                Intent intentFeedback = new Intent(DashboardActivity.this, FeedbackActivity.class);
                startActivity(intentFeedback);
                break;
            case AppConstants.FRAGMENT_PROFILE:
                Intent intentProfile = new Intent(DashboardActivity.this, ProfileActivity.class);
                startActivity(intentProfile);
                break;
            case AppConstants.FRAGMENT_ADDPRODUCT:
                Intent i= new Intent(DashboardActivity.this, AddProduct.class);
                startActivity(i);
                break;
            case AppConstants.FRAGMENT_RECIEVED_ORDERS:
                Intent intent=new Intent(DashboardActivity.this, ReceivedOrders.class);
                startActivity(intent);
                break;
        }
        if (fragment != null) {
            fragmentTransaction.replace(R.id.frame_container, fragment, fragmentTag);
            fragmentTransaction.commit();
        }
    }

    @Override
    public void addBadgeCount(boolean flag, ProductModel productModel, ProgressBar progressBar, RelativeLayout button) {
        if (flag) {
            //badgeCount--;
            for (int i = 0; i < productModelsForCart.size(); i++) {
                if (productModel.getProductId().equals(productModelsForCart.get(i).getProductId()))
                    productModelsForCart.remove(i);
            }
        } else {
            //badgeCount++;
            productModelsForCart.add(productModel);
        }
        ActionItemBadge.update(this, menu.findItem(R.id.add_to_cart), drawable, ActionItemBadge.BadgeStyles.GREY, productModelsForCart.size());
        addCartData(flag, productModelsForCart, progressBar, button);
    }

    @Override
    protected void onResume() {
        super.onResume();
//        getMenuInflater().inflate(R.menu.shopping_cart_menu, menu);
        fetchCartData();
    }

    public void fetchCartData() {
        databaseCart.orderByChild("userId").equalTo(uid).addValueEventListener(new ValueEventListener() {

            @Override
            public void onDataChange(@NonNull DataSnapshot dataSnapshot) {
                for (DataSnapshot ds : dataSnapshot.getChildren()) {
                    CartModel cartModel1 = ds.getValue(CartModel.class);
                    productModelsForCart = new ArrayList<>();
                    if (cartModel1 != null) {
                        if (cartModel1.isActive()) {
                            cartModel = cartModel1;
                            cartId = cartModel.getCartId();
                            if (cartModel.getProductModelArrayList() != null)
                                productModelsForCart.addAll(cartModel.getProductModelArrayList());
                        }
                    } else {
                        cartId = databaseCart.push().getKey();
                        cartModel = new CartModel(cartId, productModelsForCart, true, uid);
                    }
                    if (productModelsForCart != null)
                        ActionItemBadge.update(DashboardActivity.this, menu.findItem(R.id.add_to_cart), drawable, ActionItemBadge.BadgeStyles.GREY, productModelsForCart.size());
                    else
                        ActionItemBadge.update(DashboardActivity.this, menu.findItem(R.id.add_to_cart), drawable, ActionItemBadge.BadgeStyles.GREY, 0);
                }
            }

            @Override
            public void onCancelled(@NonNull DatabaseError databaseError) {
                Log.e("", "");
            }
        });
    }

