    private void addCartData(boolean flag, ArrayList<ProductModel> productModelArrayList, ProgressBar progressBar, RelativeLayout button) {
        if (cartId == null)
            cartId = databaseCart.push().getKey();
        if (cartModel == null)
            cartModel = new CartModel(cartId, productModelsForCart, true, uid);
        cartModel.setProductModelArrayList(productModelArrayList);
        databaseCart.child(cartId).setValue(cartModel).addOnCompleteListener(new OnCompleteListener<Void>() {
            @Override
            public void onComplete(@NonNull Task<Void> task) {
                progressBar.setVisibility(View.INVISIBLE);
                button.setEnabled(true);
                /*if (flag){
                    Toast.makeText(DashboardActivity.this, "Item Removed from Cart", Toast.LENGTH_SHORT).show();
                }else {
                    Toast.makeText(DashboardActivity.this, "Item Added to Cart", Toast.LENGTH_SHORT).show();
                }*/
            }
        }).addOnFailureListener(new OnFailureListener() {
            @Override
            public void onFailure(@NonNull Exception e) {
                Toast.makeText(DashboardActivity.this, "Error! Try Again Later.", Toast.LENGTH_SHORT).show();
            }
        });
    }
